sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var ga = new GlideAjax('GetCallerEmailID');
    ga.addParam('sysparm_name', 'getEmail');
    ga.addParam('caller_sys_id', g_form.getValue('caller_id'));
    ga.getXMLAnswer(processResponse);

    function processResponse(result) {
        var email = result;
        if (email) {
            g_form.setValue('x_288685_my_new_ap_email', email);
        } else {
            g_form.addErrorMessage("Email not found for the selected caller.");
        }
    }
}
