sys_script_include.script:
var CreateUser = Class.create();
CreateUser.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	setUserDetails : function(){
		var fn = this.getParameter('First_Name');
		var ln = this.getParameter('Last_Name');
		var mn = this.getParameter('MobileNumber');
		var em = this.getParameter('user_email');
		var gr = new GlideRecord('sys_user');
		gr.initialize();
		gr.user_name = fn +"."+ ln;
		gr.first_name = fn;
		gr.last_name = ln;
		gr.mobile_phone = mn;
		gr.email = em;
		gr.insert();
	},
    type: 'CreateUser'
});
