sys_script_include.script:
var CreateUserAjax = Class.create();
CreateUserAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    createUser: function() {
        var fname = this.getParameter('fname');
        var lname = this.getParameter('lname');
        var email = this.getParameter('email');

        var userGR = new GlideRecord('sys_user');
        userGR.initialize();
        userGR.first_name = fname;
        userGR.last_name = lname;
        userGR.email = email;
        userGR.user_name = fname + "." + lname;

            var userID = userGR.insert();
            if (userID) {
                return 'User created successfully with ID: ' + userID;
            } else {

                return 'User creation failed.';
            }
    },
    type: 'CreateUserAjax'
});
